client_data.forEach(client => {

    // TEMP
    if(bounds[client.client_id].min_temp) {
        min = bounds[client.client_id].min_temp 
    }
    else {
        min = 15
    }                
    if (client.temperature < min) {
        if (client.temperature < min - 5) {
            addNotification(client.client_id, "TEMP", client.temperature, "Teplota u "+client.name+" je nebezpečne nízka!", 1)
        }
        else {
            addNotification(client.client_id, "TEMP", client.temperature, "Teplota u "+client.name+" je nezvyčajne nízka!", 2)
