        max = bounds[client.client_id].max_temp 
    }
    else {
        max = 40
    }   
    if (client.temperature > max) {
        if (client.temperature > max + 5) {
            addNotification(client.client_id, "TEMP", client.temperature, "Teplota u "+client.name+" je nebezpečne vysoká!", 1)
        }
        else {
            addNotification(client.client_id, "TEMP", client.temperature, "Teplota u "+client.name+" je nezvyčajne vysoká!", 2)
        }
    }

    // HUM
    if(bounds[client.client_id].min_hum) {
        min = bounds[client.client_id].min_hum 
    }
    else {
        min = 30
    } 
    if (client.humidity < min) {
        if (client.humidity < min - 10) {
            addNotification(client.client_id, "HUM", client.humidity, "Vlkhosť vzduchu u "+client.name+" je nebezpečne nízka!", 1)
        }
        else {
            addNotification(client.client_id, "HUM", client.humidity, "Vlkhosť vzduchu u "+client.name+" je nezvyčajne nízka!", 2)
        }
    }  
    if(bounds[client.client_id].max_hum) {
        max = bounds[client.client_id].max_hum 
    }
    else {
        max = 50
    }  
    if (client.humidity > max) {
        if (client.humidity > max + 10) {
            addNotification(client.client_id, "HUM", client.humidity, "Vlkhosť vzduchu u "+client.name+" je nebezpečne vysoká!", 1)
        }
        else {
            addNotification(client.client_id, "HUM", client.humidity, "Vlkhosť vzduchu u "+client.name+" je nezvyčajne vysoká!", 2)
        }
    }

    // PRES
    if(bounds[client.client_id].min_pres) {
        min = bounds[client.client_id].min_pres 
    }
    else {
        min = 950
    } 
    if (client.pressure < min) {
        if (client.pressure < min - 5) {
            addNotification(client.client_id, "PRES", client.pressure, "Tlak u "+client.name+" je nebezpečne nízky!", 1)
        }
        else {
            addNotification(client.client_id, "PRES", client.pressure, "Tlak u "+client.name+" je nezvyčajne nízky!", 2)
        }
    }  
    if(bounds[client.client_id].max_pres) {
        max = bounds[client.client_id].max_pres 
    }
    else {
        max = 1050
    }  
    if (client.pressure > max) {
        if (client.pressure > max + 5) {
            addNotification(client.client_id, "PRES", client.pressure, "Tlak u "+client.name+" je nebezpečne vysoký!", 1)
        }
        else {
            addNotification(client.client_id, "PRES", client.pressure, "Tlak u "+client.name+" je nezvyčajne vysoký!", 2)
        }
    }

    // QUA/RES
