function addNotification(client_id, type, value, message, importance) {
    let notification = {
        client_id: client_id,
        type: type,
        value: value,
        message: message + " (" + value + ")",
        importance: importance,
    }
    new_notifications.push(notification)
}

// user client pairs
const user_client = msg.payload.user_client
// all notifications from db
const all_notifications = msg.payload.notifications
// all users
let users = msg.payload.users

users.forEach(user => {    
    let selected_clients = []
    user_client.forEach(uc_pair => {
        if(user.id == uc_pair.user_id) {
            selected_clients.push(uc_pair.client_id)
        }
    });
    user.selected_clients = selected_clients
    let notifications = []
    all_notifications.forEach(notification => {
        if(user.id == notification.user_id) {
            notifications.push(notification)
        }
    });
    user.notifications = notifications
});

msg.payload = users

return msg;

const notifications = msg.payload.notifications
const users = msg.payload.users

final_notifications = []

// notification must have selected client's id to be assigned to user
// do not insert if notification has equal type & client_id & importance with some other
notifications.forEach(notification => {

    users.forEach(user => {

        if(user.selected_clients.includes(notification.client_id)){
            if(user.notifications.length == 0){                
                new_notif = newNotification(notification)
                new_notif.user_id = user.id
                final_notifications.push(new_notif)
            }
            else {
                let exists = false
                user.notifications.forEach(usr_notif => {
                    new_notif = newNotification(notification)
                    if(usr_notif.type == new_notif.type && usr_notif.client_id == new_notif.client_id && usr_notif.importance == new_notif.importance) {
                        exists = true
                    }
                });
                if(!exists) {
                        new_notif.user_id = user.id
                        final_notifications.push(new_notif)
                    }
            }            
        }            
    });
});

msg.payload = final_notifications

return msg


function newNotification(notification) {
    let new_notif = {}    
    new_notif.client_id = notification.client_id
    new_notif.type = notification.type
    new_notif.value = notification.value
    new_notif.message = notification.message
    new_notif.importance = notification.importance
    new_notif.user_id = notification.user_id
    return new_notif
}

